        private static int GamsCheckIfTimeDimension(string identifierT, string keysi)
        {
            int i = -12345;
            if (keysi.StartsWith(identifierT, StringComparison.OrdinalIgnoreCase))
            {
                i = G.IntParse(keysi.Substring(identifierT.Length));
            }
            return i;
        }

