                    names2.Add("missingdata_exodjz");
                    names2.Add("missingdata_exod");
                    names2.Add("missingdata_exoj");
                    names2.Add("missingdata_exoz");
                }

                G.Writeln();
                G.Writeln("------------------------ Report: Find missing data -------------------------------");
                G.Writeln();
                if (Program.model == null)
                {
