            int indexYearI = -12345;
            int counter = -1;
            bool found = false;
            foreach (GekkoTime t in new GekkoTimeIterator(tStart, tEnd))  //fix for other freqs
            {
                counter++;
                if (t.IsSamePeriod(indexYear))
                {
                    found = true;
                    indexYearI = counter;
