            int labelMaxLine = 1;
            bool[] freqs = new bool[4];
                        
            int numberOfGekkoNullVariables = 0;
            int numberOfOtherVariables = 0;

            int k = -1;

            foreach (O.Prt.Element element in o.prtElements) //for each comma in the prt statement
