                            dec = options.print_fields_pdec;
                        }

                        // ---------------------------------------------
                        // --- Width -----------------------------------
                        // ---------------------------------------------
                        if (o.opt_width != -12345) width = (int)o.opt_width;
                        if (isPchType)
                        {
                            //overrides ph.width if given
                            if (o.opt_pwidth != -12345) width = (int)o.opt_pwidth;
