                        {
                            //overrides ph.width if given
                            if (element.pwidth != -12345) width = element.pwidth;
                        }
                        else
                        {
                            //overrides ph.width if given
                            if (element.nwidth != -12345) width = element.nwidth;
                        }


                        // ---------------------------------------------
                        // --- Decimals --------------------------------
                        // ---------------------------------------------
                        if (o.opt_dec != -12345) dec = (int)o.opt_dec;
