        {
            string lbl;
            string[] ss = s.Split(new string[] { Globals.freelists }, StringSplitOptions.RemoveEmptyEntries);
            if (ss.Length > 1)
            {
                lbl = ss[1];
            }
            else
            {
                lbl = s;
            }

            return lbl;
        }

