        private static int GetBankCombi(string printCode)
        {
            int bankCombi = -12345;
            List<int> bankNumbers = O.Prt.GetBankNumbers(null, new List<string> { printCode });
            if (bankNumbers.Contains(0) && !bankNumbers.Contains(1)) bankCombi = 0;
