                            G.Writeln("MAP printing not implemented yet. But individual elements can");
                            G.Writeln("be printed like for instance #m.%s, #m.x, etc.");
                        }                        
                    }
                }
            }
        }

        public static bool HandleLabels(TokenList tokenList, int level, List<O.LabelHelperIVariable> iVariableList, string[] uncontrolledSimpleLists, ref int counter)
        {
            //it is checked that the IVariables from iVariableList match in number with the relevant {...} and x{...} in the tokenList.
            //for x[..., ..., ...] they must also match in indexer position
            //if any of this is wrong, the method returns true (= fail), and in that case
            //the labels are not unfolded but just shown as they are.
            //so there will be no catastrophic fail here, just labels that are not very informative.
            //the counting check is quite a good check that the {...} and x[...] match.

            foreach (TokenHelper token in tokenList.storage)
            {
                if (token.HasChildren())
                {
                    if (token.SubnodesType() == "[" || token.SubnodesType() == "{")
                    {
                        List<TokenHelperComma> listOfTokensListsCommaSplit = token.SplitCommas();
                        int ii = -1;
                        foreach (TokenHelperComma tokenListCommaSplit in listOfTokensListsCommaSplit)  //does not include start and end parenthesis
                        {
                            counter++;
                            ii++;
                            if (tokenListCommaSplit.list.storage.Count == 2 && tokenListCommaSplit.list[0].s == Globals.symbolCollection.ToString() && tokenListCommaSplit.list[1].type == ETokenType.Word)
                            {
                                string listName = tokenListCommaSplit.list[1].s;
                                TokenHelper parent = tokenListCommaSplit.list[0];
                                bool foundAsSumFunction = false;
                                while (true)
                                {
                                    if (parent == null) break;
                                    if (parent.SubnodesType() == "(")
                                    {
                                        TokenHelper left = parent.Offset(-1);
                                        if (left != null)
                                        {
                                            if (G.Equal(left.s, "sum"))
                                            {
                                                List<TokenHelperComma> split = parent.SplitCommas();
                                                if (split.Count > 1)
                                                {
                                                    TokenList firstSplit = split[0].list;
                                                    if (firstSplit.storage.Count == 1)
                                                    {
                                                        if (firstSplit[0].SubnodesType() == "(")
                                                        {
                                                            //handles sum((#i, #j), ...)
                                                            List<TokenHelperComma> splitNew = firstSplit[0].SplitCommas();
                                                            foreach (TokenHelperComma splitNewItem in splitNew)
                                                            {
                                                                string listName2 = HandleLabelsIsSimpleListName(splitNewItem.list);
                                                                if (listName2 != null)
                                                                {
                                                                    if (G.Equal(listName, listName2))
                                                                    {
                                                                        foundAsSumFunction = true;
                                                                        break;
                                                                    }
                                                                }
                                                            }
                                                        }
                                                    }
                                                    else if (firstSplit.storage.Count > 1)
                                                    {
                                                        string listName2 = HandleLabelsIsSimpleListName(firstSplit);
                                                        if (listName2 != null)
                                                        {
                                                            //handles sum(#i, ...)

                                                            if (G.Equal(listName, listName2))
                                                            {
                                                                foundAsSumFunction = true;
                                                                break;
                                                            }
                                                        }
                                                    }
                                                }
                                            }
                                        }
                                    }
                                    parent = parent.parent;
                                }

                                if (!foundAsSumFunction)
                                {
                                    //We have a simple #x as this argument                                    
                                    //a free list has its string value put in
                                    if (counter >= iVariableList.Count)
                                    {
                                        return true;  //out of bounds, do not proceed. It seems we have more tokens found with {...} or x[...] than there are IVariables.
                                    }
                                    O.LabelHelperIVariable helper = iVariableList[counter];
                                    if (helper.index != ii)
                                    {
                                        //does not match the index position
                                        return true;
                                    }
                                    if (helper.iv.Type() == EVariableType.String)
                                    {
                                        HandleLabelsInsertIVariables(token, tokenListCommaSplit.list, O.ConvertToString(helper.iv));
                                    }
                                }
                                else
                                {
                                    //this is a bounded list, like sum(#i, x[#i]).
                                    //in that case, we just keep the #i.
                                }
                            }
                            else
                            {
                                //it is not a simple #x
                                if (counter >= iVariableList.Count)
                                {
                                    return true;  //out of bounds, do not proceed. It seems we have more tokens found with {...} or x[...] than there are IVariables.
                                }
                                O.LabelHelperIVariable helper = iVariableList[counter];
                                if (helper.index != ii)
                                {
                                    //does not match the index position
                                    return true;
                                }

                                if (helper.iv.Type() == EVariableType.String || helper.iv.Type() == EVariableType.Date || helper.iv.Type() == EVariableType.Val)
                                {
                                    HandleLabelsInsertIVariables(token, tokenListCommaSplit.list, ((ScalarString)Functions.tostring(null, helper.iv)).string2);
                                }
                            }

                        }

                    }
                    else
                    {
                        //The list only contains index values at the uppermost nesting level of [] or {} parentheses.
                        //But we handle sub-nests regarding ()-parentheses.
                        bool problem = HandleLabels(token.subnodes, level + 1, iVariableList, uncontrolledSimpleLists, ref counter);
                        if (problem)
                        {
                            return true;
                        }
                    }
                }
                else
                {
                    //s += th.leftblanks + th.s;
                }
            }
            if (level == 0 && (counter + 1 != iVariableList.Count)) //only when returning from the upmost level, where we can see if the numbers match.
            {
                return true;
            }
            return false;

        }

        private static string HandleLabelsIsSimpleListName(TokenList splitNewItem)
        {
            if (splitNewItem.storage.Count == 2 && splitNewItem[0].s == Globals.symbolCollection.ToString() && splitNewItem[1].type == ETokenType.Word)
            {
                return splitNewItem[1].s;
            }
            else return null;
        }

        private static void HandleLabelsInsertIVariables(TokenHelper th, TokenList temp2, string iv_string)
        {
            temp2[0].s = iv_string;
            temp2[0].type = ETokenType.Word;
            temp2[0].subnodes = null;
            for (int ii = 1; ii < temp2.storage.Count; ii++)
            {
                temp2[ii].s = null;
                temp2[ii].type = ETokenType.Unknown;
                temp2[ii].subnodes = null;
            }
            if (th.SubnodesType() == "{")
            {
                //Removing the '{'and '}'
                th.subnodes[0].s = null;
                th.subnodes[th.subnodes.storage.Count - 1].s = null;
            }
        }

        public static void UnfoldLabels(string elementLabel, ref List<string> labels2, List<List<O.LabelHelperIVariable>> labelHelper2)
        {
            //string elementLabel = G.ReplaceGlueNew(xelementLabel);

            labels2 = new List<string>();

            /*
             * For instance p x1[#m]+x2[#n]+sum(#k,x[#m,#n,#k]);   
             * Here freelists will be 'm' and 'n'.
             * If these are a,b and x,y we get labelHelper2 like this:
             * 
             * a x a x e
             * a y a y e
             * b x b x e
             * b y b y e
             * 
             * labelHelper2 only records inside [] or {} at the top level (not nested)
             * 
             * The tokens are like this:                 
             */
            //               
            /*

            x1 
              [
               #                    
               m                    
              ]                    
            + 
             x2 
               [
               #                    
               n                    
               ]                    
             + 
             sum 
               (
               #                    
               k                    
               , 
               x                    
                 [
                 #                    
                 m                    
                 , 
                 #                    
                 n                    
                 , 
                 #                    
                 k                    
                 ]                    
               )                    
                 EOF
                 */

            //So we walk the tokens, inserting from labelHelper2. Here, only STRING/DATE/VAL from labelHelper2are accepted, with 
            //one exception: if the token is a #x AND the token is not in freelists, we keep the '#x' and not the string from
            //labelHelper2. For instance: sum(#x, x[#x, #y, %s]). Here %s string is set in (easy). Also, the string in the
            //place of #y is set in, because 'y' is in freelists. Regarding #x, it is kept as it is.

            string[] freelists = new string[0];
            string rawLabel = elementLabel;
            string[] ss = elementLabel.Split(new string[] { Globals.freelists }, StringSplitOptions.RemoveEmptyEntries);

            if (ss.Length > 1)
            {
                rawLabel = ss[1];
                freelists = ss[0].Split(new string[] { "," }, StringSplitOptions.None);
                for (int i = 0; i < freelists.Length; i++)
                {
                    freelists[i] = freelists[i].Trim();  //there may be a blank
                }
            }

            //after this, free (uncontrolled) lists are in freelists, and rawLabel is the label.

            var tags1 = new List<Tuple<string, string>>() { new Tuple<string, string>("/*", "*/") };  //can in principle have such comments                         

            TokenHelper helperParent = StringTokenizer2.GetTokensWithLeftBlanksRecursive(rawLabel, tags1, null, null, null);
            TokenList tokens2 = helperParent.subnodes;

            if (false && Globals.runningOnTTComputer)
            {
                TokenHelper.Print(tokens2, 0);
                //TokenHelper.Print(tokens2.DeepClone(), 0);
            }

            
            foreach (List<O.LabelHelperIVariable> list in labelHelper2)
            {
                bool problem = false;
                string result = null;
                try
                {
                    int counter = -1;

                    TokenList temp = tokens2.DeepClone(helperParent);

                    problem = HandleLabels(temp, 0, list, freelists, ref counter);  //the temp object is changed here, therefore it is cloned before.                            
                    if (!problem) result = temp.ToString();
                }
                catch (Exception e)
                {
                    problem = true;
                }
                if (problem)
                {
                    //this way, there is not a complete crash, and the users get something.
                    //they may complain that the label is non-informative though.
                    //string[] sss = rawLabel.Split(new string[] { Globals.freelists }, StringSplitOptions.RemoveEmptyEntries);
                    //if (ss.Length > 1) rawLabel = ss[1];
                    labels2.Add(rawLabel);
                }
                else
                {
                    labels2.Add(result);
                }
                //
            }

            return;
        }

        private static void UnfoldLabelsHelper(List<TokenHelper> a, string list0, ref string ss2, ref int i, string freelist, ref bool flag)
        {
            if (a[i].s == "{" && a[i + 1].s == "#" && a[i + 3].s == "}" && G.Equal(a[i + 2].s, freelist))
            {
                ss2 += list0;  //no leftblanks                                
                i += 3;
                flag = true;
            }
            else if (a[i].s == "#" && G.Equal(a[i + 1].s, freelist))
            {
                ss2 += a[i].leftblanks + list0;
                i++;
                flag = true;
            }
        }

        
        private static void PrintHelper3(GekkoSmpl smpl, EPrintTypes type, EFreq sameFreq, Table table, int count, int i, int j, int iPlot, string printCode, bool isLogTransform, double scalarValueWork, Series tsWork, double scalarValueRef, Series tsRef, int year, EFreq freqColumn, int subHere, int sumOver, int[] skipCounter, O.Prt.Element cc)
        {
            string format = "f" + cc.widthFinal + "." + cc.decFinal;

            GekkoTime t = new GekkoTime(freqColumn, year, subHere);

            foreach (GekkoTime tFilter in Globals.globalPeriodTimeFilters2)
            {
                if (t.freq == tFilter.freq && t.Equals(tFilter))
                {
                    if (t.freq == EFreq.Undated) skipCounter[0]++;
                    else if (t.freq == EFreq.Annual) skipCounter[1]++;
                    else if (t.freq == EFreq.Quarterly) skipCounter[2]++;
                    else if (t.freq == EFreq.Monthly) skipCounter[3]++;
                    return;
                }
            }

            double? d = null;
            if (tsWork == null && tsRef == null)  //not series
            {
                if (sameFreq == freqColumn) d = PrintHelperTransformScalar(scalarValueWork, scalarValueRef, printCode, isLogTransform, sumOver, skipCounter);
            }
            else
            {
                if ((tsWork != null && tsWork.freq == freqColumn) || (tsRef != null && tsRef.freq == freqColumn)) d = PrintHelperTransform(smpl, tsWork, tsRef, t, printCode, isLogTransform, sumOver, skipCounter);
            }
            if (d != null)
            {
                cc.min = Math.Min(cc.min, (double)d);
                cc.max = Math.Max(cc.max, (double)d);
                if (type != EPrintTypes.Plot) table.SetNumber(i, j, (double)d, format);
                else
                {
                    double tt = ((ScalarVal)Functions.time(t)).val;                    
                    if (freqColumn == EFreq.Undated || freqColumn == EFreq.Annual) tt += 0.5;
                    //table.SetNumber(i - 1, 2 * (j - 2) + 1, tt, format);  //j=2 -> 1, j=3 -> 3
                    //table.SetNumber(i - 1, 2 * (j - 2) + 2, (double)d, format);  //j=2 -> 2, j=3 -> 4
                    table.SetNumber(i - 1, (j - 2) + 1, tt, format);                 //j=2 -> 1, j=3 -> 2
                    table.SetNumber(i - 1, (j - 2) + 1 + count, (double)d, format);  //j=2 -> 1+count, j=3 -> 2+count
                }

            }

            if (t.freq == EFreq.Undated) skipCounter[0] = 0;
            else if (t.freq == EFreq.Annual) skipCounter[1] = 0;
            else if (t.freq == EFreq.Quarterly) skipCounter[2] = 0;
            else if (t.freq == EFreq.Monthly) skipCounter[3] = 0;

        }

        private static double PrintHelperTransform(GekkoSmpl smpl, Series tsWork, Series tsRef, GekkoTime t, string printcode, bool logTransform, int sumOver, int[] skipCounter)
        {
            if (true)
            {
                //TODO filter and skip, see below
                double var1 = double.NaN;
                double varPch = double.NaN;
                Program.ComputeValueForPrintPlotNew(out var1, out varPch, printcode, t, tsWork, tsRef, logTransform, false);
                return var1;
            }
            else
            {
                int filterSkip = 0;
                if (t.freq == EFreq.Undated) filterSkip = skipCounter[0];
                else if (t.freq == EFreq.Annual) filterSkip = skipCounter[1];
                else if (t.freq == EFreq.Quarterly) filterSkip = skipCounter[2];
                else if (t.freq == EFreq.Monthly) filterSkip = skipCounter[3];

                double dWork = 0d;
                double dRef = 0d;
                double dWorkLag = 0d;
                double dWorkLag2 = 0d;
                double dRefLag = 0d;
                double dRefLag2 = 0d;
                for (int i = 0; i < sumOver + filterSkip; i++)
                {
                    if (tsWork != null)
                    {
                        dWork += tsWork.GetData(smpl, t.Add(-i));
                        dWorkLag += tsWork.GetData(smpl, t.Add(-i - 1));
                        dWorkLag2 += tsWork.GetData(smpl, t.Add(-i - 2));
                    }
                    if (tsRef != null)
                    {
                        dRef += tsRef.GetData(smpl, t.Add(-i));
                        dRefLag += tsRef.GetData(smpl, t.Add(-i - 1));
                        dRefLag2 += tsRef.GetData(smpl, t.Add(-i - 2));
                    }
                }

                //double var1 = double.NaN;
                //double varPch = double.NaN;
                //Program.ComputeValueForPrintPlotNew(out var1, out varPch, printcode, t, tsWork, tsRef, false, false);

                if (G.Equal(printcode, Globals.printCode_n)) return dWork;
                else if (G.Equal(printcode, Globals.printCode_m)) return dWork - dRef;
                else if (G.Equal(printcode, Globals.printCode_q)) return (dWork / dRef - 1d) * 100d;
                else if (G.Equal(printcode, Globals.printCode_d)) return dWork - dWorkLag;
                else if (G.Equal(printcode, Globals.printCode_p)) return (dWork / dWorkLag - 1d) * 100d;
                else if (G.Equal(printcode, Globals.printCode_dp)) return (dWork / dWorkLag - 1d) * 100d - (dWorkLag / dWorkLag2 - 1d) * 100d;
                else if (G.Equal(printcode, Globals.printCode_r) || G.Equal(printcode, Globals.printCode_rn)) return dRef;
                else if (G.Equal(printcode, Globals.printCode_rd)) return dRef - dRefLag;
                else if (G.Equal(printcode, Globals.printCode_rp)) return (dRef / dRefLag - 1d) * 100d;
                else if (G.Equal(printcode, Globals.printCode_rdp)) return (dRef / dRefLag - 1d) * 100d - (dRefLag / dRefLag2 - 1d) * 100d;
                else
                {
                    G.Writeln2("*** ERROR: Transformation error");
                    throw new GekkoException();
                }
            }
        }

        private static double PrintHelperTransformScalar(double scalarWork, double scalarRef, string printcode, bool logTransform, int sumOver, int[] skipCounter)
        {
            if (logTransform)
            {
                scalarWork = Math.Log(scalarWork);
                scalarRef = Math.Log(scalarRef);
            }

            if (G.Equal(printcode, "n")) return (sumOver * scalarWork);
            else if (G.Equal(printcode, "q")) return ((sumOver * scalarWork) / (sumOver * scalarRef) - 1d) * 100d;
            else if (G.Equal(printcode, "m")) return (sumOver * scalarWork) - (sumOver * scalarRef);
            else if (G.Equal(printcode, "d")) return 0d;
            else if (G.Equal(printcode, "p")) return 0d;
            else if (G.Equal(printcode, "dp")) return 0d;
            else
            {
                G.Writeln2("*** ERROR: Transformation error");
                throw new GekkoException();
            }
        }

        private static void PrintHelper2(List<O.PrtContainer> containerExplode, bool[] freqs, O.PrtContainer container, bool root, bool isRef, List<IVariable> errorList)
        {
            //TODO: what to do with ref, how to merge??? What if MULPRT {#m1}, and #m1 has different elements 
                      

            if (container.variable[0] != null && container.variable[0].Type() == EVariableType.Series)
            {
                containerExplode.Add(container);
                PrintFreqHelper(freqs, container.variable[0]);  //the ref one should be same freq
            }            
            else if (container.variable[0] != null && container.variable[0].Type() == EVariableType.Val)
            {
                containerExplode.Add(container);
            }
            else if (container.variable[0] != null && container.variable[0].Type() == EVariableType.Matrix && ((Matrix)container.variable[0]).data.Length == 1)  //an 1x1 matrix
            {
                containerExplode.Add(container);
            }
            else if (container.variable[0] != null && container.variable[0].Type() == EVariableType.List)
            {
                if (root)
                {
                    for(int i = 0;i< ((List)container.variable[0]).list.Count;i++)
                    {
                        O.PrtContainer c2 = new O.PrtContainer();
                        if (container.variable[0] != null) c2.variable[0] = ((List)container.variable[0]).list[i];
                        if (container.variable[0] != null) c2.variable[0] = ((List)container.variable[0]).list[i];
                        c2.label = container.label;
                        c2.printCode = container.printCode;
                        PrintHelper2(containerExplode, freqs, c2, false, isRef, errorList);  //the counter is fixed
                    }
                }
            }
            else
            {
                errorList.Add(container.variable[0]);                            
            }
        }

        private static void PrintFreqHelper(bool[] freqs, IVariable iv)
        {
            if (((Series)iv).freq == EFreq.Annual) freqs[0] = true;
            else if (((Series)iv).freq == EFreq.Quarterly) freqs[1] = true;
            else if (((Series)iv).freq == EFreq.Monthly) freqs[2] = true;
        }

