        private static string GnuplotText(string s, bool omitCurly)
        {
            //cf. http://ayapin-film.sakura.ne.jp/Gnuplot/Docs/ps_guide.pdf
            if (s == null) return null;
            string s2 = s;
            s2 = s2.Replace(@"_", @"\\_");
            s2 = s2.Replace(@"@", @"\\@");
            if (!omitCurly)
            {
                s2 = s2.Replace(@"{", @"\\{");
                s2 = s2.Replace(@"}", @"\\}");
            }
            s2 = s2.Replace(@"^", @"\\^");
            s2 = s2.Replace(@"&", @"\\&");
            return s2;            
        }

