        private static int GnuplotHandleXAxisLabels(List<string> labels1, int mxtics, out List<int> subperiods, out int onlyYears)
        {
            subperiods = new List<int>();
            onlyYears = -12345;
            if (Program.options.freq == EFreq.Quarterly)
            {
                if (labels1.Count <= 12)  //for quarterly, 12 corresponds to 3 years with 4 subpers each
                {
                    subperiods.Add(1);  //q1
                    subperiods.Add(4);  //q2
                    subperiods.Add(7);  //q3
                    subperiods.Add(10);  //q4
                }
                else if (labels1.Count <= 24)
                {
                    subperiods.Add(1);  //q1
                    subperiods.Add(7);  //q3
                    mxtics = 2;
                }
                else if (labels1.Count <= 48)
                {
                    subperiods.Add(1);  //q1
                    mxtics = 4;
                }
                else if (labels1.Count <= 5 * 48)
                {
                    onlyYears = 5;
                    subperiods.Add(1);  //q1
                    mxtics = 5;
                }
                else if (labels1.Count <= 10 * 48)
                {
                    onlyYears = 10;
                    subperiods.Add(1);  //q1
                    mxtics = 10;
                }
                else
                {
                    onlyYears = 20;
                    subperiods.Add(1);  //q1
                }
            }
            else  //monthly
            {
                if (labels1.Count <= 12)  //for monthly, 12 corresponds to 1 year with 12 subpers
                {
                    subperiods.Add(1);  //m1
                    subperiods.Add(2);  //m2
                    subperiods.Add(3);  //m3
                    subperiods.Add(4);  //m4
                    subperiods.Add(5);  //m5
                    subperiods.Add(6);  //m6
                    subperiods.Add(7);  //m7
                    subperiods.Add(8);  //m8
                    subperiods.Add(9);  //m9
                    subperiods.Add(10);  //m10
                    subperiods.Add(11);  //m11
                    subperiods.Add(12);  //m12
                }
                else if (labels1.Count <= 24)
                {
                    subperiods.Add(1);  //m1
                    subperiods.Add(3);  //m3
                    subperiods.Add(5);  //m5
                    subperiods.Add(7);  //m7
                    subperiods.Add(9);  //m9
                    subperiods.Add(11);  //m11
                    mxtics = 2;
                }
                else if (labels1.Count <= 36)
                {
                    subperiods.Add(1);  //m1
                    subperiods.Add(4);  //m4
                    subperiods.Add(7);  //m7
                    subperiods.Add(10);  //m10
                    mxtics = 3;
                }
                else if (labels1.Count <= 48)
                {
                    subperiods.Add(1);  //m1
                    subperiods.Add(5);  //m5
                    subperiods.Add(9);  //m9
                    mxtics = 4;
                }
                else if (labels1.Count <= 72)
                {
                    subperiods.Add(1);  //m1
                    subperiods.Add(7);  //m7
                    mxtics = 6;
                }
                else if (labels1.Count <= 144)
                {
                    subperiods.Add(1);  //m1
                }
                else if (labels1.Count <= 15 * 48)
                {
                    onlyYears = 5;
                    subperiods.Add(1);  //m1
                    mxtics = 5;
                }
                else if (labels1.Count <= 30 * 48)
                {
                    onlyYears = 10;
                    subperiods.Add(1);  //m1
                    mxtics = 10;
                }
                else
                {
                    onlyYears = 20;
                    subperiods.Add(1);  //m1
                }
            }

            return mxtics;
        }

        private static string GnuplotYrange(string ymin, string yminsoft, string yminhard, string ymax, string ymaxsoft, string ymaxhard)
        {
            // [  yminhard < * < yminsoft  : ymaxsoft < * < ymaxhard ] 
            // TODO: it would be nice to test the inequalities above, because if they are violated, they have no effect --> free borders, even if ymin/ymax is set.

            if (G.Equal(ymin, "NaN")) ymin = null;
            if (G.Equal(yminsoft, "NaN")) yminsoft = null;
            if (G.Equal(yminhard, "NaN")) yminhard = null;
            if (G.Equal(ymax, "NaN")) ymax = null;
            if (G.Equal(ymaxsoft, "NaN")) ymaxsoft = null;
            if (G.Equal(ymaxhard, "NaN")) ymaxhard = null;

            string left = null;
            string right = null;
            if (!G.NullOrEmpty(ymin))
            {                
                left = ymin;
            }
            else
            {                
                if (!G.NullOrEmpty(yminhard) && !G.NullOrEmpty(yminsoft))
                {
                    left = yminhard + " < * < " + yminsoft;
                }
                else if (!G.NullOrEmpty(yminhard) && G.NullOrEmpty(yminsoft))
                {
                    left = yminhard + " < * ";
                }
                else if (G.NullOrEmpty(yminhard) && !G.NullOrEmpty(yminsoft))
                {
                    left = " * < " + yminsoft;
                }
                else
                {
                    left = "";
                }
            }

            if (!G.NullOrEmpty(ymax))
            {
                double xx = ParseIntoDouble(ymax);  //just testing
                right = ymax;
            }
            else
            {
                if (!G.NullOrEmpty(ymaxhard) && !G.NullOrEmpty(ymaxsoft))
                {
                    right = ymaxsoft + " < * < " + ymaxhard;
                }
                else if (!G.NullOrEmpty(ymaxhard) && G.NullOrEmpty(ymaxsoft))
                {
                    right = " * < " + ymaxhard;
                }
                else if (G.NullOrEmpty(ymaxhard) && !G.NullOrEmpty(ymaxsoft))
                {
                    right = ymaxsoft + " < * ";
                }
                else
                {
                    right = "";
                }
            }
            return left + ":" + right;
        }            

