            Process p = new Process();
            //p.MainWindowTitle = title;
            p.StartInfo.FileName = Application.StartupPath + "\\gnuplot\\wgnuplot.exe";
            //p.StartInfo.WindowStyle = ProcessWindowStyle.Hidden;
