                res2.SetValue(i1, res[i1]);
                x2.SetValue(i1, x[i1]);
            }
            numericalProblem = runModel(res2, x2, assembly);  //res is residuals, implicitly also calculates b array
            for (int i1 = 0; i1 < n; i1++)
            {
                res[i1] = res2.GetValue(i1);
                //x[i1]=x2.GetValue(i1);  //unaltered
            }
            return double.NaN;
        }

