    public class HPfilter
    {
        public double[] HPFilter(double[] data, double lambda)
        {
            {
                {
                    int N = data.Length;
                    int K;
                    double[] a = new double[N];
                    double[] b = new double[N];
                    double[] c = new double[N];

                    // Elemente der Haupt- und Nebendiagonalen

                    a[0] = 1 + lambda;
                    b[0] = -2 * lambda;
                    c[0] = lambda;
                    for (K = 1; K < N - 2; K++)
                    {
                        a[K] = 6 * lambda + 1;
                        b[K] = -4 * lambda;
                        c[K] = lambda;
                    }
                    a[1] = 5 * lambda + 1;
                    a[N - 1] = 1 + lambda;
                    a[N - 2] = 5 * lambda + 1;
                    b[0] = -2 * lambda;
                    b[N - 2] = -2 * lambda;
                    b[N - 1] = 0;
                    c[N - 2] = 0;
                    c[N - 1] = 0;

                    data = pentas(a, b, c, data, N);

                    return data;


                }
            }
        }

        static double[] pentas(double[] a, double[] b, double[] c, double[] data, int N)
        {
            /*
             *  Schneller Algorithmus zur Lösung des LGS BxX=Y
             * unter Berücksichtigung, dass B eine pentadiagonale Matrix ist
             * siehe z.B. Helmut Späth "Numerik, Eine Einführung für
             * Mathematiker und Informatiker", S. 110 ff, Braunschweig &
             * Wiesbaden, 1994
             *
            */

            int K;
            double H1 = 0;
            double H2 = 0;
            double H3 = 0;
            double H4 = 0;
            double H5 = 0;
            double HH1 = 0;
            double HH2 = 0;
            double HH3 = 0;
            double HH4 = 0;
            double HH5 = 0;
            double Z;
            double HB;
            double HC;

            // Vorwärts
            for (K = 0; K < N; K++)
            {
                Z = a[K] - H4 * H1 - HH5 * HH2;
                HB = b[K];
                HH1 = H1;
                H1 = (HB - H4 * H2) / Z;
                b[K] = H1;
                HC = c[K];
                HH2 = H2;
                H2 = HC / Z;
                c[K] = H2;
                a[K] = (data[K] - HH3 * HH5 - H3 * H4) / Z;
                HH3 = H3;
                H3 = a[K];
                H4 = HB - H5 * HH1;
                HH5 = H5;
                H5 = HC;
            }

            // Rückwärts
            H2 = 0;
            H1 = a[N - 1];
            data[N - 1] = H1;

            for (K = N - 2; K > -1; K--)
            {

                data[K] = a[K] - b[K] * H1 - c[K] * H2;
                H2 = H1;
                H1 = data[K];
            }
            return data;
        }
    }

